    static void insertImage(Context context){
        Cursor query = CharacterDataBase.getInstances(context).query();
        File nf = new File(Environment.getExternalStorageDirectory()+"/FateDictionary");
        nf.mkdir();

        if (query.moveToFirst()) {
            do {
                int number = query.getInt(query.getColumnIndex("number"));
                String str = "a"+number;
                if (getResId(str)>0){
                    Bitmap imageBig = BitmapFactory.decodeResource(context.getResources(),getResId(str));
                    File f1 = new File(Environment.getExternalStorageDirectory()+"/FateDictionary", str+".png");
                    FileOutputStream out1 = null;
                    try {
                        out1 = new FileOutputStream(f1);
                        imageBig.compress(Bitmap.CompressFormat.PNG, 100, out1);
                        try {
                            out1.flush();
                            out1.close();
                        } catch (IOException e) {
                            e.printStackTrace();
                        }

                    } catch (FileNotFoundException e) {
                        e.printStackTrace();
                    }
                }
                if (getResId(str+"_little")>0){
                    Bitmap imageLittle = BitmapFactory.decodeResource(context.getResources(),getResId(str+"_little"));
                    File f2 = new File(Environment.getExternalStorageDirectory()+"/FateDictionary", str+"_little.png");
                    FileOutputStream out2 = null;
                    try {
                        out2 = new FileOutputStream(f2);
                        imageLittle.compress(Bitmap.CompressFormat.PNG, 100, out2);
                        try {
                            out2.flush();
                            out2.close();
                        } catch (IOException e) {
                            e.printStackTrace();
                        }

                    } catch (FileNotFoundException e) {
                        e.printStackTrace();
                    }
                }


            }while (query.moveToNext());
        }
    }

    private static int getResId(String imageName){
        Class mipmap = R.mipmap.class;
        try {
            Field field = mipmap.getField(imageName);
            return field.getInt(imageName);
        } catch (NoSuchFieldException | IllegalAccessException e) {//如果没有在"mipmap"下找到imageName,将会返回0
            return 0;
        }

    }
